import React from "react";
import "./form.css";
import useInput from "./input hooks/use-input";

function Form() {
  const {
    enteredData: address,
    handleEnteredData: handleAddress,
    isValid: addressIsValid,
    onBlurChangeHandler : onAddressBlurChangeHandler
  } = useInput(enteredData => enteredData !== '');

  const onSubmitHandler = (event) => {
        event.preventDefault()
        onAddressBlurChangeHandler()
        if(!addressIsValid){
          return
        }
  }

  return (
    <>
      <form onSubmit={onSubmitHandler}>
        <div>
          <label htmlFor="address">Address</label>
          <input type="text" id="address" onChange={handleAddress} onBlur={onAddressBlurChangeHandler}/>
          {!addressIsValid && (
            <p style={{ color: "red" }}>Sorry address is invalid</p>
          )}
        </div>
        <div>
          <label htmlFor="email">Email</label>
          <input type="email" id="email" />
        </div>
        <button className="submit-btn">Submit</button>
      </form>
    </>
  );
}

export default Form;
