import React from "react";
import "./form.css";
import useInput from "./input hooks/use-input";

function Form() {
  const {
    enteredData: address,
    handleEnteredData: handleAddress,
    isValid: addressIsValid,
    onBlurChangeHandler: onAddressBlurChangeHandler,
    reset: resetAddress,
  } = useInput((enteredData) => enteredData !== "");

  const {
    enteredData: enteredEmail,
    handleEnteredData: handleEmail,
    isValid: emailIsValid,
    onBlurChangeHandler: onEmailBlurChangeHandler,
    reset: resetEmail,
  } = useInput(
    (enteredData) => enteredData !== "" && enteredData.trim().includes("@")
  );

  const onSubmitHandler = (event) => {
    event.preventDefault();
    onAddressBlurChangeHandler();
    if (!addressIsValid) {
      return;
    }
    resetAddress();
  };

  return (
    <>
      <form onSubmit={onSubmitHandler}>
        <div>
          <label htmlFor="address">Address</label>
          <input
            type="text"
            id="address"
            onChange={handleAddress}
            onBlur={onAddressBlurChangeHandler}
            value={address}
          />
          {!addressIsValid && (
            <p style={{ color: "red" }}>Sorry address is invalid</p>
          )}
        </div>
        <div>
          <label htmlFor="email">Email</label>
          <input type="email" id="email" onChange={handleEmail} onBlur={onEmailBlurChangeHandler} value={enteredEmail} />
        </div>
        <button className="submit-btn">Submit</button>
      </form>
    </>
  );
}

export default Form;
