import { useState } from "react"

const useInput = (condition) => {
    const [enteredData,setEnteredData] = useState()
    const [isValid,setIsValid] = useState(false)
    const [enteredDataIsTouched,setEnteredDataIsTouched] = useState(false)

    const isInvalid = !isValid && enteredDataIsTouched

    const handleEnteredData = (event) => {
        
        setEnteredData(event.target.value)
        setEnteredDataIsTouched(true)
        if(condition!= ''){
            setIsValid(true)
        }
        
    }

    const onBlurChangeHandler = (event) => {
        setEnteredDataIsTouched(true)
    }

   const onSubmitHandler = (event) => {
    event.preventDefault()
    setEnteredDataIsTouched(true)
   }

   const reset = () => {
    
   }

    return {
        enteredData,
        handleEnteredData,
        isValid,
        onSubmitHandler,
        onBlurChangeHandler,
        isInvalid
    }
}

export default useInput
